<script>
function submit_welcome_call(id) {
    var status = $("#call_status_welcome" + id).val();
    var remark_welcome = $("#remark_welcome" + id).val();

    // Check for required fields
    var requiredFields = $("#welcome_call_form_submit" + id).find("[required]");
    var errorMessages = [];

    requiredFields.each(function() {
        var field = $(this);
        if (field.val().trim() === '') {
            errorMessages.push(field.attr('name') || field.attr('id'));
        }
    });

    // If there are any empty required fields, show toastr error
    if (errorMessages.length > 0) {
        toastr.options.timeOut = 10000;
        toastr.error("Please enter remark of not ok status");
        return;
    }

    if (status == 'Completed') {
        var count = $(".messages" + id + " input[type='radio']:checked").length;
        var rating = $("#rating" + id + " input[type='radio']:checked").length;
        if (count > 4 && rating > 0) {
            var raise_issue = $("#raise_issue" + id + ":checked").length;
            if (raise_issue > 0) {
                if (remark_welcome != '') {
                    $("#welcome_call_form_submit" + id).submit();
                } else {
                    toastr.options.timeOut = 10000;
                    toastr.error("Please enter remark");
                }
            } else {
                $("#welcome_call_form_submit" + id).submit();
            }
        } else {
            toastr.options.timeOut = 10000;
            toastr.error("Please select all message status and Counsellor rating");
        }
    } else if (status == 'Not Completed') {
        $("#welcome_call_form_submit" + id).submit();
    } else {
        toastr.options.timeOut = 10000;
        toastr.error("Please select call status");
    }
}
</script>
